template <typename T>
struct type {
  static const char *string() {
    static const char *s[] = {
        "unknown",    "unknown8",  "unknown16", "unknown24",  "unknown32",
        "unknown40",  "unknown48", "unknown56", "unknown64",  "unknown72",
        "unknown80",  "unknown88", "unknown96", "unknown104", "unknown112",
        "unknown120", "unknown128"};
    return s[(sizeof(T) < 17) ? sizeof(T) : 0];
  }
  static bool is_float() { return false; }
  static bool is_inf(const T) { return false; }
  static bool is_nan(const T) { return false; }
  static T min() { return ~max(); }
  static T max() { return (T)(1UL << (8 * sizeof(T) - 1)); }
  static T inf() { return max(); }
  static T cut(const double val) {
    return val < (double)min() ? min() : val > (double)max() ? max() : (T)val;
  }
  static const char *format() { return "%s"; }
  static const char *format(const T &val) {
    static const char *const s = "unknown";
    cimg::unused(val);
    return s;
  }
};

