//! Return the minimum between two values.
template <typename t1, typename t2>
inline typename cimg::superset<t1, t2>::type min(const t1 &a, const t2 &b) {
  typedef typename cimg::superset<t1, t2>::type t1t2;
  return (t1t2)(a <= b ? a : b);
}

