//! Return the basename of a filename.
inline const char *basename(const char *const s,
                            const char separator = cimg_file_separator) {
  const char *p = 0, *np = s;
  while (np >= s && (p = np)) np = std::strchr(np, separator) + 1;
  return p;
}

// Return a random filename.
inline const char *filenamerand() {
  cimg::mutex(6);
  static char randomid[9];
  cimg::srand();
  for (unsigned int k = 0; k < 8; ++k) {
    const int v = (int)cimg::rand(65535) % 3;
    randomid[k] =
        (char)(v == 0 ? ('0' + ((int)cimg::rand(65535) % 10))
                      : (v == 1 ? ('a' + ((int)cimg::rand(65535) % 26))
                                : ('A' + ((int)cimg::rand(65535) % 26))));
  }
  cimg::mutex(6, 0);
  return randomid;
}

