#define _cimg_create_ext_operators(typ)                                 \
  template <typename T>                                                 \
  inline CImg<typename cimg::superset<T, typ>::type> operator+(         \
      const typ val, const CImg<T> &img) {                              \
    return img + val;                                                   \
  }                                                                     \
  template <typename T>                                                 \
  inline CImg<typename cimg::superset<T, typ>::type> operator-(         \
      const typ val, const CImg<T> &img) {                              \
    typedef typename cimg::superset<T, typ>::type Tt;                   \
    return CImg<Tt>(img._width, img._height, img._depth, img._spectrum, \
                    val) -= img;                                        \
  }                                                                     \
  template <typename T>                                                 \
  inline CImg<typename cimg::superset<T, typ>::type> operator*(         \
      const typ val, const CImg<T> &img) {                              \
    return img * val;                                                   \
  }                                                                     \
  template <typename T>                                                 \
  inline CImg<typename cimg::superset<T, typ>::type> operator/(         \
      const typ val, const CImg<T> &img) {                              \
    return val * img.get_invert();                                      \
  }                                                                     \
  template <typename T>                                                 \
  inline CImg<typename cimg::superset<T, typ>::type> operator&(         \
      const typ val, const CImg<T> &img) {                              \
    return img & val;                                                   \
  }                                                                     \
  template <typename T>                                                 \
  inline CImg<typename cimg::superset<T, typ>::type> operator|(         \
      const typ val, const CImg<T> &img) {                              \
    return img | val;                                                   \
  }                                                                     \
  template <typename T>                                                 \
  inline CImg<typename cimg::superset<T, typ>::type> operator^(         \
      const typ val, const CImg<T> &img) {                              \
    return img ^ val;                                                   \
  } template <typename T>                                               \
  inline bool operator==(const typ val, const CImg<T> &img) {           \
    return img == val;                                                  \
  }                                                                     \
  template <typename T>                                                 \
  inline bool operator!=(const typ val, const CImg<T> &img) {           \
    return img != val;                                                  \
  }

