  //! Return \c true if specified key sequence has been typed on the associated
  // window, \c false otherwise.
  /**
     \param keycodes_sequence Buffer of keycodes to test.
     \param length Number of keys in the \c keycodes_sequence buffer.
     \param remove_sequence Tells if the key sequence must be removed from the
  key history, if found.
     \note Keycode constants are defined in the cimg namespace and are
  architecture-dependent. Use them to ensure
     your code stay portable (see cimg::keyESC).
     \par Example
     \code
     CImgDisplay disp(400,400);
     const unsigned int key_seq[] = { cimg::keyCTRLLEFT, cimg::keyD };
     while (!disp.is_closed()) {
       if (disp.is_key_sequence(key_seq,2)) { ... }  // Test for the 'CTRL+D'
  keyboard event.
       disp.wait();
     }
     \endcode
  **/
  bool is_key_sequence(const unsigned int *const keycodes_sequence,
                       const unsigned int length,
                       const bool remove_sequence = false) {
    if (keycodes_sequence && length) {
      const unsigned int *const ps_end = keycodes_sequence + length - 1,
                                *const pk_end =
                                    (unsigned int *)_keys + 1 + 128 - length,
                                k = *ps_end;
      for (unsigned int *pk = (unsigned int *)_keys; pk < pk_end;) {
        if (*(pk++) == k) {
          bool res = true;
          const unsigned int *ps = ps_end, *pk2 = pk;
          for (unsigned int i = 1; i < length; ++i) res = (*(--ps) == *(pk2++));
          if (res) {
            if (remove_sequence)
              std::memset((void *)(pk - 1), 0, sizeof(unsigned int) * length);
            return true;
          }
        }
      }
    }
    return false;
  }

