  //! Display list of images on associated window.
  /**
     \param list List of images to display.
     \param axis Axis used to append the images along, for the visualization
  (can be \c x, \c y, \c z or \c c).
     \param align Relative position of aligned images when displaying lists with
  images of different sizes
     (\c 0 for upper-left, \c 0.5 for centering and \c 1 for lower-right).
     \note This method returns immediately.
  **/
  template <typename T>
  CImgDisplay &display(const CImgList<T> &list, const char axis = 'x',
                       const float align = 0) {
    if (list._width == 1) {
      const CImg<T> &img = list[0];
      if (img._depth == 1 && (img._spectrum == 1 || img._spectrum >= 3) &&
          _normalization != 1)
        return display(img);
    }
    CImgList<typename CImg<T>::ucharT> visu(list._width);
    cimglist_for(list, l) {
      const CImg<T> &img = list._data[l];
      img.__get_select(*this, _normalization, (img._width - 1) / 2,
                       (img._height - 1) / 2,
                       (img._depth - 1) / 2).move_to(visu[l]);
    }
    visu.get_append(axis, align).display(*this);
    return *this;
  }

