  //! Simulate a mouse button release event.
  /**
     \note All mouse buttons are considered released at the same time.
  **/
  CImgDisplay &set_button() {
    _button = 0;
    _is_event = true;
#if cimg_display == 1
    pthread_cond_broadcast(&cimg::X11_attr().wait_event);
#elif cimg_display == 2
    SetEvent(cimg::Win32_attr().wait_event);
#endif
    return *this;
  }

  //! Simulate a mouse button press or release event.
  /**
     \param button Buttons event code, where each button is associated to a
  single bit.
     \param is_pressed Tells if the mouse button is considered as pressed or
  released.
  **/
  CImgDisplay &set_button(const unsigned int button,
                          const bool is_pressed = true) {
    const unsigned int buttoncode =
        button == 1U ? 1U : button == 2U ? 2U : button == 3U ? 4U : 0U;
    if (is_pressed)
      _button |= buttoncode;
    else
      _button &= ~buttoncode;
    _is_event = buttoncode ? true : false;
    if (buttoncode) {
#if cimg_display == 1
      pthread_cond_broadcast(&cimg::X11_attr().wait_event);
#elif cimg_display == 2
      SetEvent(cimg::Win32_attr().wait_event);
#endif
    }
    return *this;
  }

