  template <typename t>
  CImg<T> &operator%=(const CImg<t> &img) {
    const unsigned long siz = size(), isiz = img.size();
    if (siz && isiz) {
      if (is_overlapped(img)) return *this %= +img;
      T *ptrd = _data, *const ptre = _data + siz;
      if (siz > isiz)
        for (unsigned long n = siz / isiz; n; --n)
          for (const t *ptrs = img._data, *ptrs_end = ptrs + isiz;
               ptrs < ptrs_end; ++ptrd)
            *ptrd = cimg::mod(*ptrd, (T) * (ptrs++));
      for (const t *ptrs = img._data; ptrd < ptre; ++ptrd)
        *ptrd = cimg::mod(*ptrd, (T) * (ptrs++));
    }
    return *this;
  }

