  bool operator==(const char *const expression) const {
    return *this ==
           (+*this)._fill(expression, true, true, 0, 0, "operator==", this);
  }

  //! Test if two images have the same size and values.
  /**
     Return \c true if the image instance and the input image \c img have the
  same dimensions and pixel values,
     and \c false otherwise.
     \param img Input image to compare with.
     \note
     - The pixel buffer pointers data() of the two compared images do not have
  to be the same for operator==()
       to return \c true.
       Only the dimensions and the pixel values matter. Thus, the comparison can
  be \c true even for different
       pixel types \c T and \c t.
     \par Example
     \code
     const CImg<float> img1(1,3,1,1, 0,1,2); // Construct a 1x3 vector [0;1;2]
  (with 'float' pixel values).
     const CImg<char> img2(1,3,1,1, 0,1,2);  // Construct a 1x3 vector [0;1;2]
  (with 'char' pixel values).
     if (img1==img2) {                       // Test succeeds, image dimensions
  and values are the same.
       std::printf("'img1' and 'img2' have same dimensions and values.");
     }
     \endcode
  **/
  template <typename t>
  bool operator==(const CImg<t> &img) const {
    typedef _cimg_Tt Tt;
    const unsigned long siz = size();
    bool is_equal = true;
    if (siz != img.size()) return false;
    t *ptrs = img._data + siz;
    for (T *ptrd = _data + siz; is_equal && ptrd > _data;
         is_equal = ((Tt) * (--ptrd) == (Tt) * (--ptrs))) {
    }
    return is_equal;
  }

