  //! Fill all pixel values with specified value.
  /**
     \param val Fill value.
  **/
  CImg<T> &fill(const T &val) {
    if (is_empty()) return *this;
    if (val && sizeof(T) != 1)
      cimg_for(*this, ptrd, T) *ptrd = val;
    else
      std::memset(_data, (int)val, sizeof(T) * size());
    return *this;
  }

