    float ntx0 = tx0 / z0, nty0 = ty0 / z0, ntx1 = tx1 / z1, nty1 = ty1 / z1,
          ntx2 = tx2 / z2, nty2 = ty2 / z2, nz0 = 1 / z0, nz1 = 1 / z1,
          nz2 = 1 / z2;
