  //! Draw a text string.
  /**
     \param x0 X-coordinate of the text in the image instance.
     \param y0 Y-coordinate of the text in the image instance.
     \param text Format of the text ('printf'-style format string).
     \param foreground_color Pointer to \c spectrum() consecutive values,
  defining the foreground drawing color.
     \param background_color Pointer to \c spectrum() consecutive values,
  defining the background drawing color.
     \param opacity Drawing opacity.
     \param font Font used for drawing text.
  **/
  template <typename tc1, typename tc2, typename t>
  CImg<T> &draw_text(const int x0, const int y0, const char *const text,
                     const tc1 *const foreground_color,
                     const tc2 *const background_color, const float opacity,
                     const CImgList<t> &font, ...) {
    if (!font) return *this;
    CImg<charT> tmp(2048);
    std::va_list ap;
    va_start(ap, font);
    cimg_vsnprintf(tmp, tmp._width, text, ap);
    va_end(ap);
    return _draw_text(x0, y0, tmp, foreground_color, background_color, opacity,
                      font, false);
  }

