  //! Return a C-string containing the values of all images in the instance
  // list.
  /**
     \param separator Character separator set between consecutive pixel values.
     \param max_size Maximum size of the returned string.
     \note The result is returne as a <tt>CImg<char></tt> image whose pixel
  buffer contains the desired C-string.
  **/
  CImg<charT> value_string(const char separator = ',',
                           const unsigned int max_size = 0) const {
    if (is_empty()) return CImg<ucharT>(1, 1, 1, 1, 0);
    CImgList<charT> items;
    for (unsigned int l = 0; l < _width - 1; ++l) {
      CImg<charT> item = _data[l].value_string(separator, 0);
      item.back() = separator;
      item.move_to(items);
    }
    _data[_width - 1].value_string(separator, 0).move_to(items);
    CImg<charT> res;
    (items > 'x').move_to(res);
    if (max_size) {
      res.crop(0, max_size);
      res(max_size) = 0;
    }
    return res;
  }

