#define _cimglist_def_is_same1(axis)                                        \
  bool is_same##axis(const unsigned int val) const {                        \
    bool res = true;                                                        \
    for (unsigned int l = 0; l < _width && res; ++l)                        \
      res = _data[l].is_same##axis(val);                                    \
    return res;                                                             \
