#define _cimglist_def_is_same(axis)                                     \
  template <typename t>                                                 \
  bool is_same##axis(const CImg<t> &img) const {                        \
    bool res = true;                                                    \
    for (unsigned int l = 0; l < _width && res; ++l)                    \
