  //! Insert \c n copies of the image \c img into the current image list, at
  // position \c pos.
  /**
     \param n Number of image copies to insert.
     \param img Image to insert by copy.
     \param pos Index of the insertion.
     \param is_shared Tells if inserted images are shared copies of \c img or
  not.
  **/
  template <typename t>
  CImgList<T> &insert(const unsigned int n, const CImg<t> &img,
                      const unsigned int pos = ~0U,
                      const bool is_shared = false) {
    if (!n) return *this;
    const unsigned int npos = pos == ~0U ? _width : pos;
    insert(img, npos, is_shared);
    for (unsigned int i = 1; i < n; ++i)
      insert(_data[npos], npos + i, is_shared);
    return *this;
  }

