 function [F] = snoptuserfun(x)
%function [F] = snoptuserfun(x)
% Computes F for the hexagon problem.

F   = [  x(2)*x(6) - x(1)*x(7) + x(3)*x(7) + x(5)*x(8) - x(4)*x(9) - x(3)*x(8);
         x(1)^2          +  x(6)^2;
        (x(2) - x(1))^2  +  (x(7) - x(6))^2;
        (x(3) - x(1))^2  +  x(6)^2;
        (x(1) - x(4))^2  +  (x(6) - x(8))^2;
        (x(1) - x(5))^2  +  (x(6) - x(9))^2;
         x(2)^2          +  x(7)^2;
        (x(3) - x(2))^2  +  x(7)^2;;
        (x(4) - x(2))^2  +  (x(8) - x(7))^2;
        (x(2) - x(5))^2  +  (x(7) - x(9))^2;
        (x(4) - x(3))^2  +  x(8)^2;
        (x(5) - x(3))^2  +  x(9)^2;
         x(4)^2          +  x(8)^2;
        (x(4) - x(5))^2  +  (x(9) - x(8))^2;
         x(5)^2          +  x(9)^2;
        -x(1) + x(2);
        -x(2) + x(3);
         x(3) - x(4);
         x(4) - x(5)    ];

